import React, { useState, useEffect } from 'react';
import { Drawer, Button, Col, Row, Table, Divider, Input, Space ,Image,message} from 'antd';

// import { Table, Input, Button, Space } from 'antd';
import Highlighter from 'react-highlight-words';
import { SearchOutlined } from '@ant-design/icons';

import {
  selectTheBillOfLading
} from './RadioPageForFaxBillOfLadingServer';

const RadioPageForFaxBillOfLading = ({
  showDrawerOpen,
  height,
  visible,
  params,
  submitRecord
}) => {

  const [rowRecord, setRowRecord] = useState({})

  const submit = () => {
    submitRecord(rowRecord[0]);
    setRowRecord({});
  }

  const [tableData, settableData] = useState(Array)

  let errorIamge = "data:image/png;base64,";
  const [nullImage,setNullImage] = useState('')
  const [ImageUrl ,setImageUrl] = useState('');
  useEffect( () => {
    if(visible){
      testFunc()
    }
    
  }, [visible]);

  const testFunc = async()=>{
    if(visible){
      setNullImage(errorIamge);
      let data =await selectTheBillOfLading(params);
      if(data.status == '200'){
        settableData(data.data);
      }else{
        return message.error(data.message);
      }
    }
  }


  return (
    <>


      <App 
      url={ImageUrl}
      nullImage={nullImage}
      tableData={tableData}
      visible={visible}
      rowRecord={rowRecord}
      submit={submit}
      showDrawerOpen={showDrawerOpen}
      height={700}
      setRowRecord={setRowRecord}
      setImageUrl={setImageUrl}
      setNullImage={setNullImage}
      />
    </>
  );

}

class App extends React.Component {
 
  state = {
    searchText: '',
    searchedColumn: '',

    
  };


  getColumnSearchProps = dataIndex => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={node => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            搜索
          </Button>
        
          <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
            重置
          </Button>
        </Space>
      </div>
    ),
    filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
        : '',
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: text =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ''}
        />
      ) : (
          text
        ),
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };
  

  render() {
    const {
      tableData,
      visible,
      url,
      nullImage,
      rowRecord,
      submit,
      showDrawerOpen,
      height,
      setRowRecord,
      setImageUrl,
      setNullImage,
    } = this.props


  const _column=[
    
      {
        title: '传真编号',
        dataIndex: 'no',
        width:100,
        // ...this.getColumnSearchProps('no'),
      },
      {
        title: '提单号',
        dataIndex: 'doNo',
        width:100,
        // ...this.getColumnSearchProps('doNo'),
      },
      {
        title: '客户',
        dataIndex: 'customer',
        width:100,
        // ...this.getColumnSearchProps('customer'),
      },
      {
        title: '货品',
        dataIndex: 'commodity',
        width:100,
        // ...this.getColumnSearchProps('commodity'),
      }
    ]

    
  
  const  rowSelection = {
    onChange: (selectedRowKeys, selectedRows) => {
      console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
      setImageUrl(selectedRows[0].filePath);
      setNullImage('')
      setRowRecord(selectedRows);
    }
  };

 
    return (
      <div>
        <Drawer
          title={`传真提单-列表选择`}
          placement={'bottom'}
          closable={true}
          onClose={() => showDrawerOpen(false)}
          visible={visible}
          key={'bottom'}
          height={height}
          destroyOnClose={true}
        >
          <div>
          <Row>
              <Row gutter={24}>
                <Col span={9}>
                  <Button disabled={!rowRecord.length} key="submit" type="primary" onClick={() => submit()}>
                    提交
                </Button>
                </Col>

              </Row>
  
              <Row gutter={24}>
                <Col span={12}>
                <Table
             rowSelection={{
               type: 'radio',
               ...rowSelection,
             }}
             bordered
             scroll={{x:500,y: 400 }}
             rowKey={'no'}
             columns={_column}
             
             dataSource={tableData}
              pagination={false}
           />
                </Col>
<Col span={12}>
<Image
      width={'100%'}
      height={400}
      style={{'maxHeight':'400px'}}
      src={url}
      fallback={nullImage}
    />
</Col>
              </Row>
            </Row> 

            <Divider />
           
          </div>
        </Drawer>
      </div>
    )
  }
}



export default RadioPageForFaxBillOfLading



